command:
/**
 * Simulacrum Example Tool
 * 
 * HOW TO CREATE YOUR OWN AI TOOLS:
 * 1. Create a new macro (World or compendium - anywhere works)
 * 2. Define the 'tool' constant with your configuration (see below)
 * 3. Set 'enabled: true' to activate the tool for AI use
 * 4. Save the macro - it will be discovered automatically!
 *
 * TEMPLATE:
 */

const tool = {
    name: "flash_token",
    description: "Flashes a target token with a specific color intensity.",
    enabled: false, // Set to true to enable this tool for AI use
    parameters: {
        type: "object",
        properties: {
            targetTokenId: {
                type: "string",
                description: "The ID of the token to flash."
            },
            intensity: {
                type: "number",
                description: "Intensity of the flash (1-10)."
            }
        },
        required: ["targetTokenId", "intensity"]
    }
};

// --- Tool Logic Below ---
// Arguments are passed in 'scope.args'

const { targetTokenId, intensity } = scope.args || {};

if (!targetTokenId || !intensity) {
    console.log("Simulacrum Tool: Run by AI to see effect.");
    return;
}

const token = canvas.tokens.get(targetTokenId);
if (token?.mesh?.flash) {
    token.mesh.flash(intensity > 5 ? 0xFF0000 : 0x00FF00);
    return { success: true, message: "Flashed token " + token.name };
}
